package Assignments;

public class LearnCharacterIndex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String text = "Java Exercise" ;
	
	System.out.println("Index of 'E'  is :" +text.indexOf('E'));
	System.out.println("Index of 's'  is :" +text.indexOf('s'));
	System.out.println("Charecter at index 7  is : "+text.charAt(7));
	
	}

}
